);

SELECT 'create table continent';
CREATE TABLE continent (
    id_continent INT  NOT NULL AUTO_INCREMENT,
    nom varchar(50)  NOT NULL ,
    CONSTRAINT pk_id_continent PRIMARY KEY (id_continent)
);


SELECT 'create table region';
CREATE TABLE region (
    id_region INT  NOT NULL AUTO_INCREMENT,
    nom varchar(50)  NOT NULL ,
    id_continent INT NOT NULL,
    CONSTRAINT pk_id_region PRIMARY KEY (id_region),
    CONSTRAINT fk_id_continent FOREIGN KEY(id_continent) REFERENCES continent(id_continent)
);

SELECT 'create table ville';
CREATE TABLE ville (
    id_ville INT  NOT NULL AUTO_INCREMENT,
    nom varchar(50)  NOT NULL ,
    region INT  NOT NULL ,
    CONSTRAINT pk_id_lieu PRIMARY KEY (id_ville),
    CONSTRAINT fk_region FOREIGN KEY(region) REFERENCES region (id_region)
);

SELECT 'create table Guerre';
CREATE TABLE Guerre (
    id_guerre INT  NOT NULL AUTO_INCREMENT,
    nom varchar(250)  NOT NULL ,
    id_lieu  INT(5),
    annee_deb INT,
    annee_fin INT,
    CONSTRAINT pk_id_guerre PRIMARY KEY (id_guerre)
);

SELECT 'create table deplacement';
Create table deplacement(
    id_personnage int (5),
    id_ville_dep int (5),
    id_ville_dest int (5),
    CONSTRAINT fk_id_perso_deplacement FOREIGN KEY(id_personnage) REFERENCES Personnage (id_personnage),
    CONSTRAINT fk_id_lieu_deplacement_dep FOREIGN KEY(id_ville_dep) REFERENCES ville (id_ville),
    CONSTRAINT fk_id_lieu_deplacement_dest FOREIGN KEY(id_ville_dest) REFERENCES ville (id_ville)
);

